package sensesolutions;

//=============================================================================
//
//=============================================================================
public class Measurement
{

    private long start = 0;
    private long stop = 0;

    //=============================================================================
    //
    //=============================================================================
    public Measurement ( )
    {
        // TODO Auto-generated constructor stub
    }

    //=============================================================================
    //
    //=============================================================================
    public void Start ( )
    {
        start = System.nanoTime();
    }

    //=============================================================================
    //
    //=============================================================================
    public void Stop ( )
    {
        stop = System.nanoTime();
    }

    //=============================================================================
    //
    //=============================================================================
    public long GetDurationInMicroSec ( )
    {
        long duration = ( stop - start ) / 1000;
        return duration;
    }

    //=============================================================================
    //
    //=============================================================================
    public long GetDurationInMiliSec  ( )
    {
        long duration = ( stop - start ) / 1000000;
        return duration;
    }
    //=============================================================================
    //
    //=============================================================================
    public Double GetDurationInSec( )
    {
        Double duration = Double.valueOf (( stop - start ) / 1000000000);
        return duration;
    }

    public long GetCurrentTimeInMiliSec  ( )
    {
        return System.nanoTime()/1000000;
    }

    //=============================================================================
    //
    //=============================================================================
    public long GetCurrentTimeInMicroSec  ( )
    {
        return System.nanoTime()/1000;
    }
    //=============================================================================
    //
    //=============================================================================
    public long GetCurrentTimeInNanoSec  ( )
    {
        return System.nanoTime();
    }

}

